company_name like #{company_name}

    and production_type like #{production_type}


    and manage_type like #{manage_type}


    and web_type like #{web_type}

select * from companyinfo where
company_name like #{company_name}

    and production_type like #{production_type}


    and manage_type like #{manage_type}


    and web_type like #{web_type}

